import React, {useState} from 'react';
import {useNavigate} from "react-router-dom";
import App from '../App';
const API_BASE="http://localhost:4000";

const useAuth = async () => {
    
    const navigate = useNavigate();
    
    const [errorMessage, setErrorMessage] = React.useState("");

    try {
        const response = await fetch(API_BASE + '/api/v1/userdashboard', {
            method: "GET",
        });

        const convertedRespones = await response.json();
        // console.log(convertedRespones);
        // // console.log("Hi");
        return convertedRespones.success;
    } catch (error) {
        setErrorMessage("You are Not ALLowed This Source...")
        console.log(error);
        navigate("/");
        console.log("hi");
        return false;
    }
}

const ProtectedRoutes = () => {
    const navigate = useNavigate();
    const isAuth = useAuth();
    console.log(isAuth);

    return isAuth ? (<App />) : (navigate("/"));
}

export default ProtectedRoutes;
