import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import React, { useState } from 'react'
import './TweetBox.css'
// import db from './firebase'

function TweetBox() {
    const [tweetMessage, setTweetMessage] = useState("");
    // const [tweetImage, setTweetImage] = useState("");
    const [file, setFile] = useState(null);

    const sendTweet = e => {
        e.preventDefault();

        // db.collection('posts').add({
        //     displayName : "CodeWithAkky",
        //     username: "akky_im",
        //     verified: true,
        //     text: tweetMessage,
        //     image: tweetImage,
        //     avatar: "https://pbs.twimg.com/profile_images/1266938830608875520/f-eajIjB_400x400.jpg"
        // })

        setTweetMessage("");
        setFile();
    }

    return (
        <div className = "tweetBox">
            <form>
                <div className = "tweetBox__input">
                    <Avatar
                        src = "https://res.cloudinary.com/ddxco6opc/image/upload/v1691867097/statuscode/sh7iwxyinjjnq2njkugv.jpg"
                    />
                    <input 
                        onChange = {(e) => setTweetMessage(e.target.value)}
                        value = {tweetMessage} 
                        placeholder = "What's happening" 
                        type = "text" 
                    />
                </div>
                {/* <input 
                    onChange = { (e) => setTweetImage(e.target.value) }
                    value = {tweetImage}
                    className = "tweetBox__imageInput"
                    placeholder = "Optional : Enter Image URL"
                    type = "text"
                /> */}
                <input type="file" placeholder="Optional : Enter Image URL" className = "tweetBox__imageInput"
                name="file" accept="image/*"
                onChange={(e) => setFile(e.target.files[0])}/>
                <Button 
                onClick = { sendTweet }
                className = "tweetBox__tweetButton">Post</Button>
            </form>
        </div>
    )
}

export default TweetBox
