import React, { useEffect, useState } from 'react'
import './Feed.css'
import TweetBox from './TweetBox'
import Post from './Post'
import FlipMove from 'react-flip-move'
const API_BASE="http://localhost:4000";

function Feed() {
    const [posts, setPosts] = useState({
        tweets: [],
        countTweets: 0
    });

    useEffect(() => {
        fetch(API_BASE + '/api/v1/getalltweets', {
            method: "GET",
            credentials: "include",
        })
        .then( (data) => setPosts({ tweets:data.sortedtweets, countTweets:data.countTweets }))
        .catch((error) => {
            //setError(error);
            console.log(error);
        });
    }, [])

    console.log(posts.countTweets);

    return (
        <div className = "feed">
            <div className = "feed__header">
                <h2>Home</h2>
            </div>

            <TweetBox />
            {/* <Post />
            <Post />
            <Post />
            <Post /> */}
            <FlipMove>
                {posts.sortedtweets.map(post => (
                <Post 
                key = {post.tweet_text}
                displayName = {post.user.name}
                username = {post.user.username}
                verified = {true}
                text = {post.tweet_text}
                image = {post.tweet_image.secure_url}
            />
            ))}
            </FlipMove>   
        </div>
    )
}
export default Feed;
